  }

  &-pattern {
    position: absolute;
    right: 10%;
    bottom: -3%;
    width: 60%;
    z-index: 2;

    & img {
      width: 100%;
    }
  }
}

#locations-choice-container {
  margin-top: 120px;
}

.locations-choice {
  position: relative;
  width: 90%;
  height: auto;
  max-width: 1400px;
  margin-inline: auto;
  @include c.flex;
  z-index: 3;
}

.location-choice {
