@use 'config' as c;

@media (max-width: 768px) {
  .hero {
    @include c.flex;
    flex-direction: column;
    height: auto;

    &-contents {
      margin-right: 0px;
      text-align: center;

      & .hero-header {
        font-size: map-get(c.$font-sizes, H1);
      }

      & .hero-copy {
        width: 70%;
        margin-inline: auto;
      }
    }
  }

  .hero-pattern {
    right: -20%;
  }

  .cores {
    grid-template-columns: repeat(1, 1fr);
    grid-template-rows: repeat(4, 1fr);
    grid-row-gap: 30px;
    grid-column-gap: 0px;

    &.graphic {
      grid-template-rows: repeat(3, 1fr);
    }
  }

  .core {
    @include c.flex;
    border-radius: 12px;
    width: 100%;

    &-img {
      width: 380px;
      width: 50%;
      height: 100%;
      border-radius: 0px 0px 0 0px;

      & img {
        width: 100%;
        height: 100%;
      }
    }

    &-content {
      display: flex;
      justify-content: center;
      align-items: center;
      flex-direction: column;
      height: 100%;
      margin-top: 0px;

      &-header {
        font-size: map-get(c.$font-sizes, H3);
        font-weight: c.$font-weight_medium;
        margin-bottom: 10px;
      }

      &-copy {
        font-size: map-get(c.$font-sizes, body);
      }
    }
  }

  .services {
    grid-template-columns: auto;
    grid-template-rows: repeat(2, 1fr);
    grid-gap: 20px;

    & .service {
      height: 250px;
      &:hover {
        &:after {
        }
      }

      &.web {
        background-image: url('../../assets/home/desktop/web-design-small.png');

        &:after {
          left: 0;
          width: 0%;
          height: 100%;
        }

        &:hover {
          &:after {
            width: 100%;
          }
        }
      }
    }
  }

  .cta {
    flex-direction: column;
    background: c.$pri_peach;
    border-radius: 12px;
    padding: 50px 70px;

    &-content {
      width: 80%;
      margin-inline: auto;
      text-align: center;

      &-header {
        margin-bottom: 20px;
      }

      &-copy {
        font-size: map-get(c.$font-sizes, body);
      }
    }

    &-btn {
      margin-top: 30px;
      & a {
        @include c.button_on_dark;
      }
    }
  }

  .footer-bottom {
    &-content {
      & .address {
        margin-right: 30px;
      }
    }
  }

  .bg-pattern {
    display: none;
  }
}
