@use 'config' as c;

@media (max-width: 768px) {
  .hero {
    @include c.flex;
    flex-direction: column;
    padding-bottom: 0;
    height: 843px;

    &-contents {
      margin-right: 0px;
      text-align: center;

      & .hero-header {
        font-size: map-get(c.$font-sizes, H1);
      }

      & .hero-copy {
        width: 70%;
        margin-inline: auto;
      }
    }

    &-oval {
      right: -30px;
      top: 45px;
    }

    &-img {
      width: 300px;
      transform: translateY(5%);
    }
  }

  .services {
    grid-template-columns: auto;
    grid-template-rows: repeat(3, 1fr);
    grid-template-areas:
      'web'
      'app'
      'graphic';
    grid-gap: 20px;

    & .service {
      height: 200px;
      &:hover {
        &:after {
        }
      }

      &.web {
        background-image: url('../../assets/home/desktop/web-design-small.png');

        &:after {
          left: 0;
          width: 0%;
          height: 100%;
        }

        &:hover {
          &:after {
            width: 100%;
          }
        }
      }

      &.app {
        height: 200px;
      }

      &.graphic {
        height: 200px;
      }
    }
  }

  #values-container {
    margin-top: 80px;
  }

  .values {
    flex-direction: column;

    & .value {
      flex-direction: row;

      &:not(:last-child) {
        margin-right: 0px;
        margin-bottom: 50px;
      }

      &-content {
        width: 70%;
        text-align: left;
        margin-left: 30px;

        & .value-heading {
          margin-bottom: 10px;
        }
      }
    }
  }

  .cta {
    flex-direction: column;
    background: c.$pri_peach;
    border-radius: 12px;
    padding: 50px 70px;

    &-content {
      width: 80%;
      margin-inline: auto;
      text-align: center;

      &-header {
        margin-bottom: 20px;
      }

      &-copy {
        font-size: map-get(c.$font-sizes, body);
      }
    }

    &-btn {
      margin-top: 30px;
      & a {
        @include c.button_on_dark;
      }
    }
  }

  .footer-bottom {
    &-content {
      & .address {
        margin-right: 30px;
      }
    }
  }

  .bg-pattern {
    display: none;
  }
}

@media (max-width: 600px) {
  .header {
    & .links {
      position: absolute;
      top: 93px;
      left: 0;
      flex-direction: column;
      align-items: flex-start;
      justify-content: flex-start;
      width: 100%;
      transform: translateX(100%);
      z-index: 5;
      @include c.transition;

      &:after {
        position: absolute;
        content: '';
        top: 0;
        left: 0;
        width: 100%;
        height: 100vh;
        background: c.$pri_black;
        opacity: 0.5;
        z-index: 4;
        display: none;
      }

      &.open {
        transform: translateX(0);
        &:after {
          display: block;
        }
      }

      & .link {
        background: c.$pri_black;
        width: 100%;
        padding: 20px 0;
        padding-left: 30px;
        z-index: 5;
        transform: translateX(100%);

        @include c.transition;
        z-index: 5;

        &.open {
          transform: translateX(0);
        }

        &:first-child {
          padding-top: 50px;
        }
        &:last-child {
          padding-bottom: 50px;
        }

        &:not(:last-child) {
          margin-right: 0px;
          margin-bottom: -10px;
        }

        & a {
          font-size: 25px;
          color: c.$pri_white;

          &:hover {
            &:after {
              width: 0;
            }
          }
        }
      }
    }

    & .menu-icon {
      display: block;

      & i {
        font-size: 30px;
        cursor: pointer;

        &.fa-times {
          display: none;
        }
      }

      &.open {
        & .fa-bars {
