    width: 100%;
  }

  .hero {
    width: 100%;
    border-radius: 0px;
    height: 1100px;
    padding-top: 120px;
    z-index: 6;

    &-contents {
      margin-right: 0px;
      text-align: center;
      width: 90%;
      margin-bottom: 50px;

      & .hero-header {
        font-size: 32px;
      }

      & .hero-copy {
        font-size: 15px;
        width: 100%;
        margin-inline: auto;
      }

      & .hero-btn {
