      &.app {
        height: 200px;
      }

      &.graphic {
        height: 200px;
      }

      & .content {
        & .service-type {
          font-size: 28px;
          margin-bottom: 10px;
        }

        & .link {
          font-size: 15px;
        }
      }
    }
  }

  .values {
    & .value {
      flex-direction: column;
      justify-content: center;
      align-items: center;

      &:not(:last-child) {
        margin-right: 0px;
        margin-bottom: 50px;
      }

      &-content {
