@use 'config' as c;
@import 'reset';

body {
  font-family: c.$font-family;
  background: c.$pri_white;
  overflow-x: hidden;
  min-width: 100%;
}

#header-container {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 94.5px;
  background: c.$pri_white;
  padding: 30px 0;
  z-index: 10;
  border-bottom: 1px solid c.$pri_black;
}

.header {
  @include c.flex;
  align-items: flex-start;
  width: 90%;
  max-width: 1400px;
  margin-inline: auto;

  & .logo {
    width: 196px;

    & img {
      width: 100%;
    }
  }

  & .links {
    @include c.flex;

    & .link {
      &:not(:last-child) {
        margin-right: 25px;
      }

      & a {
        position: relative;
        font-size: map-get(c.$font-sizes, links);
